import React, { useEffect, useState, useContext, useMemo } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { BarGraph } from "src/components";
import axios from "axios";

import * as S from "./styled";

export const ShowMyPlan = () => {
  let { id } = useParams(); // Ïû¨Ìï†Îãπ Í∞ÄÎä•
  let navigate = useNavigate();
  const [listOfPosts, setListOfPosts] = useState([
    {
      createdAt: "",
    },
  ]);
  const [timeData, setTimeData] = useState({});
  const [authState, setAuthState] = useState({
    id: 0,
    status: false,
  });

  //10Î≤à Ïú†Ï†ÄÏùò 3Î≤àÎç∞Ïù¥ÌÑ∞ Ï¶â :id/:idÎäêÎÇåÏúºÎ°ú Í∞ÄÏïºÌï†ÎìØ Ïù¥Í≤å ÎêòÎÇò?
  //ÏïÑÎãàÎ©¥ ÏûîÏ©å ÎπÑÌö®Ïú®Ï†ÅÏù¥Í≤å Ìïú ÌéòÏù¥ÏßÄÎ•º Îòê ÎßåÎì§Ïñ¥ÏÑú Ìï¥Î≥¥Ïûê ÎÇ¥Ïùº
  useEffect(() => {
    axios
      .get(`http://localhost:8080/todaytimes/byuserId/${id}`)
      .then((response) => {
        setListOfPosts(response.data);
      });
    axios
      .get("http://localhost:8080/auth/auth", {
        headers: {
          accessToken: localStorage.getItem("accessToken") as any,
        },
      })
      .then((res) => {
        if (res.data.error) {
          setAuthState({ ...authState, status: false });
        } else {
          setAuthState({
            id: res.data.id,
            status: true,
          });
        }
      });
    axios.get(`http://localhost:8080/todaytimes/byId/${id}`).then((res) => {
      setListOfPosts(res.data);
      setTimeData(res.data);
    });
    if (!localStorage.getItem("accessToken")) {
      navigate("/auth/login");
      alert("Î°úÍ∑∏Ïù∏ÎêòÏñ¥ÏûàÎÇòÏöî?ü§î");
    }
  }, []);

  return (
    <S.AllContainer>
      <S.Slider>
        <S.TotalContainer>
          <S.DayContainer>
            {listOfPosts.map((value: any, key: any) => {
              return (
                <div key={key}>
                  <S.DayTime
                    onClick={() => {
                      navigate(`/timedata/${value.id}`);
                    }}
                  >
                    {value.createdAt.slice(5, 10)}
                  </S.DayTime>
                </div>
              );
            })}
          </S.DayContainer>
        </S.TotalContainer>
      </S.Slider>
    </S.AllContainer>
  );
};
