import React, { useEffect, useState, useContext, useMemo } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { BarGraph } from "src/components";
import axios from "axios";

import * as S from "./styled";

export const ShowMyPlan = () => {
  let { id } = useParams(); // 재할당 가능
  let navigate = useNavigate();
  const [listOfPosts, setListOfPosts] = useState([
    {
      user_id: "",
      createdAt: "",
    },
  ]);
  const [Posts, setPosts] = useState({
    kor: 0,
    eng: 0,
    math: 0,
    sci: 0,
    com: 0,
    kh: 0,
    study: 0,
  });
  const [UserId, setUserId] = useState({});
  const [authState, setAuthState] = useState({
    id: 0,
    status: false,
  });

  //10번 유저의 3번데이터 즉 :id/:id느낌으로 가야할듯 이게 되나?
  //아니면 잔쩌 비효율적이게 한 페이지를 또 만들어서 해보자 내일
  useEffect(() => {
    axios
      .get(`http://localhost:8080/todaytimes/byuserId/${id}`)
      .then((response) => {
        setListOfPosts(response.data);
      });

    if (!localStorage.getItem("accessToken")) {
      navigate("/auth/login");
      alert("로그인되어있나요?🤔");
    }
    axios
      .get("http://localhost:8080/auth/auth", {
        headers: {
          accessToken: localStorage.getItem("accessToken") as any,
        },
      })
      .then((res) => {
        if (res.data.error) {
          setAuthState({ ...authState, status: false });
        } else {
          setAuthState({
            id: res.data.id,
            status: true,
          });
        }
      });
    axios.get(`http://localhost:8080/todaytimes/byId/${id}`).then((res) => {
      setUserId(res.data);
      setPosts(res.data);
    });
  }, []);

  return (
    <S.AllContainer>
      <S.Slider>
        <S.TotalContainer>
          <S.DayContainer>
            {listOfPosts.map((value: any, key: any) => {
              return (
                <div key={key}>
                  <S.DayTime
                    onClick={() => {
                      navigate(`/myinfo/myplan/${value.user_id}/${value.id}`);
                    }}
                  >
                    {value.createdAt.slice(5, 10)}
                  </S.DayTime>
                </div>
              );
            })}
          </S.DayContainer>
        </S.TotalContainer>
      </S.Slider>
    </S.AllContainer>
  );
};
