import React, { useEffect, useState, useContext, useMemo } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { BarGraph } from "src/components";
import axios from "axios";

import * as S from "./styled";

export const ShowMyPlan = () => {
  let { id } = useParams(); // ìž¬í• ë‹¹ ê°€ëŠ¥
  let navigate = useNavigate();
  const [listOfPosts, setListOfPosts] = useState([
    {
      user_id: "",
      createdAt: "",
    },
  ]);
  const [Posts, setPosts] = useState({
    kor: 0,
    eng: 0,
    math: 0,
    sci: 0,
    com: 0,
    kh: 0,
    study: 0,
  });
  const [UserId, setUserId] = useState({});
  const [authState, setAuthState] = useState({
    id: 0,
    status: false,
  });
  const subData = useMemo(
    () => [
      {
        id: 1,
        sub: "êµ­ì–´",
        time: timeData.kor,
      },
      {
        id: 2,
        sub: "ì˜ì–´",
        time: timeData.eng,
      },
      {
        id: 3,
        sub: "ìˆ˜í•™",
        time: timeData.math,
      },
      {
        id: 4,
        sub: "ê³¼í•™",
        time: timeData.sci,
      },
      {
        id: 5,
        sub: "ì‚¬íšŒ",
        time: timeData.com,
      },
      {
        id: 6,
        sub: "í•œêµ­ì‚¬",
        time: timeData.kh,
      },
      {
        id: 7,
        sub: "ê³µë¶€",
        time: timeData.study,
      },
    ],
    [timeData]
  );

  const time = useMemo(
    () => ({
      labels: subData.map((val) => val.sub),
      datasets: [
        {
          label: "ì‹œê°„ë°ì´í„°",
          data: subData.map((val) => val.time),
          backgroundColor: [
            "rgba(255, 99, 132, 0.2)",
            "rgba(255, 159, 64, 0.2)",
            "rgba(255, 205, 86, 0.2)",
            "rgba(75, 192, 192, 0.2)",
            "rgba(54, 162, 235, 0.2)",
            "rgba(153, 102, 255, 0.2)",
            "rgba(84, 221, 154, 0.2)",
          ],
          borderColor: [
            "rgb(255, 99, 132)",
            "rgb(255, 159, 64)",
            "rgb(255, 205, 86)",
            "rgb(75, 192, 192)",
            "rgb(54, 162, 235)",
            "rgb(153, 102, 255)",
            "rgb(84, 221, 154)",
          ],
          borderWidth: 1,
        },
      ],
    }),
    [subData]
  );

  //10ë²ˆ ìœ ì €ì˜ 3ë²ˆë°ì´í„° ì¦‰ :id/:idëŠë‚Œìœ¼ë¡œ ê°€ì•¼í• ë“¯ ì´ê²Œ ë˜ë‚˜?
  //ì•„ë‹ˆë©´ ìž”ì©Œ ë¹„íš¨ìœ¨ì ì´ê²Œ í•œ íŽ˜ì´ì§€ë¥¼ ë˜ ë§Œë“¤ì–´ì„œ í•´ë³´ìž ë‚´ì¼
  useEffect(() => {
    axios
      .get(`http://localhost:8080/todaytimes/byuserId/${id}`)
      .then((response) => {
        setListOfPosts(response.data);
      });

    if (!localStorage.getItem("accessToken")) {
      navigate("/auth/login");
      alert("ë¡œê·¸ì¸ë˜ì–´ìžˆë‚˜ìš”?ðŸ¤”");
    }
    axios
      .get("http://localhost:8080/auth/auth", {
        headers: {
          accessToken: localStorage.getItem("accessToken") as any,
        },
      })
      .then((res) => {
        if (res.data.error) {
          setAuthState({ ...authState, status: false });
        } else {
          setAuthState({
            id: res.data.id,
            status: true,
          });
        }
      });
    axios.get(`http://localhost:8080/todaytimes/byId/${id}`).then((res) => {
      setUserId(res.data);
      setPosts(res.data);
    });
  }, []);

  return (
    <S.AllContainer>
      <S.Slider>
        <S.TotalContainer>
          <S.DayContainer>
            {listOfPosts.map((value: any, key: any) => {
              return (
                <div key={key}>
                  <S.DayTime
                    onClick={() => {
                      navigate(`/myinfo/myplan/${value.user_id}/${value.id}`);
                    }}
                  >
                    {value.createdAt.slice(5, 10)}
                  </S.DayTime>
                </div>
              );
            })}
          </S.DayContainer>
        </S.TotalContainer>
      </S.Slider>
    </S.AllContainer>
  );
};
