import React, { useState, useEffect } from "react";
import axios from "axios";

import * as S from "./styled";

export const Intro = () => {
  const [authState, setAuthState] = useState({
    id: "",
    status: false,
  });

  useEffect(() => {
    axios
      .get("http://localhost:8080/auth/auth", {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        },
      })
      .then((res) => {
        if (res.data.error) {
          setAuthState({ ...authState, status: false });
        } else {
          setAuthState({
            id: res.data.id,
            status: true,
          });
        }
      });
  }, []);
  return (
    <S.Background>
      <S.MainContainer>
        <S.SectionContainer to="/time/today">
          <S.SectionOne>
            <S.TitleContainer>
              <S.SectionTitle>공부시간 적으러 가기</S.SectionTitle>
            </S.TitleContainer>
          </S.SectionOne>
        </S.SectionContainer>
        <S.SectionContainer to={`/myinfo/myplan/${authState.id}`}>
          <S.SectionTwo>
            <S.TitleContainer>
              <S.SectionTitle>공부시간 적으러 가기</S.SectionTitle>
            </S.TitleContainer>
          </S.SectionTwo>
        </S.SectionContainer>
      </S.MainContainer>
    </S.Background>
  );
};
