import React, { useEffect, useState, useMemo } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

import { Horizontal, HorizontalContainer } from "../MyTime/styled";

import * as S from "./styled";
import { BarGraph } from "../GraphList";
import { host } from "src/LinkHost";

export const TimeData = () => {
  let { id } = useParams();
  const [timeData, setTimeData] = useState({});
  const [habitData, setHabitData] = useState({});
  const [goalData, setGoalData] = useState({});
  const [authState, setAuthState] = useState({
    id: 0,
    user_id: "",
    status: false,
  });
  const [listOfPosts, setListOfPosts] = useState([
    {
      createdAt: "",
    },
  ]);
  const lifeStyle = useMemo(
    () => [
      {
        id: 1,
        habit: "Í∏∞ÏÉÅÏãúÍ∞ÑH",
        habitTime: habitData.morning,
      },
      {
        id: 2,
        habit: "Ï∑®Ïπ®ÏãúÍ∞ÑH",
        habitTime: habitData.night,
      },
      {
        id: 3,
        habit: "ÎÇÆÏû†ÏãúÍ∞ÑH",
        habitTime: habitData.sleep,
      },
      {
        id: 4,
        habit: "Ìú¥ÏãùÏãúÍ∞ÑH",
        habitTime: habitData.break,
      },
    ],
    [habitData]
  );
  const subData = useMemo(
    () => [
      {
        id: 1,
        sub: "Íµ≠Ïñ¥",
        time: timeData.kor,
      },
      {
        id: 2,
        sub: "ÏòÅÏñ¥",
        time: timeData.eng,
      },
      {
        id: 3,
        sub: "ÏàòÌïô",
        time: timeData.math,
      },
      {
        id: 4,
        sub: "Í≥ºÌïô",
        time: timeData.sci,
      },
      {
        id: 5,
        sub: "ÏÇ¨Ìöå",
        time: timeData.com,
      },
      {
        id: 6,
        sub: "ÌïúÍµ≠ÏÇ¨",
        time: timeData.kh,
      },
      {
        id: 7,
        sub: "Í≥µÎ∂Ä",
        time: timeData.study,
      },
    ],
    [timeData]
  );
  const lifeStyleData = useMemo();
  const time = useMemo(
    () => ({
      labels: subData.map((val) => val.sub),
      datasets: [
        {
          label: "ÏãúÍ∞ÑÎç∞Ïù¥ÌÑ∞",
          data: subData.map((val) => val.time),
          backgroundColor: [
            "rgba(255, 99, 132, 0.2)",
            "rgba(255, 159, 64, 0.2)",
            "rgba(255, 205, 86, 0.2)",
            "rgba(75, 192, 192, 0.2)",
            "rgba(54, 162, 235, 0.2)",
            "rgba(153, 102, 255, 0.2)",
            "rgba(84, 221, 154, 0.2)",
          ],
          borderColor: [
            "rgb(255, 99, 132)",
            "rgb(255, 159, 64)",
            "rgb(255, 205, 86)",
            "rgb(75, 192, 192)",
            "rgb(54, 162, 235)",
            "rgb(153, 102, 255)",
            "rgb(84, 221, 154)",
          ],
          borderWidth: 1,
        },
      ],
    }),
    [subData]
  );

  useEffect(() => {
    axios
      .get(`http://${host}:8080/auth/auth`, {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        },
      })
      .then((res) => {
        if (res.data.error) {
          setAuthState({ ...authState, status: false });
        } else {
          setAuthState({
            id: res.data.id,
            user_id: res.data.user_id,
            status: true,
          });
        }
      });
    axios.get(`http://${host}:8080/todaytimes/byId/${id}`).then((res) => {
      setListOfPosts(res.data);
      setTimeData(res.data);
    });
  }, []);
  console.log(listOfPosts);
  return (
    <>
      <S.TimeDataContainer>
        <S.BackSpaceContainer>
          <S.BackSpace to={`/myinfo/myplan/${authState.user_id}`}>
            &lt;-
          </S.BackSpace>
        </S.BackSpaceContainer>
        <S.TimeTextContainer>
          <S.TimeText>
            üóì {listOfPosts.createdAt} | {listOfPosts.UserId}
          </S.TimeText>
        </S.TimeTextContainer>
        <HorizontalContainer>
          <Horizontal />
        </HorizontalContainer>
        {authState.id === listOfPosts.UserId ? (
          <S.TimeDataBoxContainer>
            <BarGraph chartData={time} />
          </S.TimeDataBoxContainer>
        ) : (
          <div
            style={{
              textAlign: "center",
              marginTop: "3rem",
              fontSize: "2rem",
            }}
          >
            Îã§Î•∏ ÌïôÏÉùÏùò Îç∞Ïù¥ÌÑ∞Ïóê Ï†ëÍ∑ºÌñàÏñ¥Ïöîü§î
          </div>
        )}
      </S.TimeDataContainer>
    </>
  );
};
