import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Formik, Form, Field } from "formik";
import { Button } from "src/components/common";
import * as Yup from "yup";
import axios from "axios";
import * as S from "./styled";
import Stack from "@mui/material/Stack";
import Snackbar from "@mui/material/Snackbar";
import MuiAlert, { AlertProps } from "@mui/material/Alert";

const Alert = React.forwardRef<HTMLDivElement, AlertProps>(function Alert(
  props,
  ref
) {
  return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});

export interface RegisterFormInputProps
  extends React.InputHTMLAttributes<HTMLInputElement> {}
export const RegisterForm = React.forwardRef<
  HTMLInputElement,
  RegisterFormInputProps
>(({ autoComplete, placeholder, type, name }, ref) => {
  const initialValues = {
    user_name: "",
    user_id: "",
    user_pw: "",
  };
  const [open, setOpen] = React.useState(false);
  const navigate = useNavigate();

  const validationSchema = Yup.object().shape({
    user_name: Yup.string().required(),
    user_id: Yup.string().required(),
    user_pw: Yup.string().required(),
  });

  const onSubmit = (data: any) => {
    axios.post("http://localhost:8080/auth", data).then(() => {
      console.log(data);
      window.location.replace("/auth/register");
      setOpen(true);
    });
  };
  const handleClose = (
    event?: React.SyntheticEvent | Event,
    reason?: string
  ) => {
    if (reason === "clickaway") {
      return;
    }

    setOpen(false);
  };
  useEffect(() => {
    if (localStorage.getItem("accessToken")) {
      navigate("/");
      alert("Î°úÍ∑∏Ïù∏ÎêòÏñ¥ ÏûàÏäµÎãàÎã§.");
    }
  }, []);

  return (
    <S.FormInputContainer ref={ref}>
      <S.InputElementContainer>
        <S.InputContainer>
          <Stack spacing={2} sx={{ width: "100%" }}>
            <Formik
              initialValues={initialValues}
              onSubmit={onSubmit}
              validationSchema={validationSchema}
            >
              <Form>
                <S.Label>Ïù¥Î¶Ñ</S.Label>
                <S.InputElement
                  autoComplete="off"
                  name="user_name"
                  placeholder="Ïù¥Î¶Ñ"
                />
                <S.Label>ÏïÑÏù¥Îîî</S.Label>
                <S.InputElement
                  type="text"
                  autoComplete="off"
                  name="user_id"
                  placeholder="id"
                />
                <S.Label>ÎπÑÎ∞ÄÎ≤àÌò∏</S.Label>
                <S.InputElement
                  autoComplete="off"
                  name="user_pw"
                  placeholder="pw"
                  type="password"
                />
                <Button type="submit" onClick={onSubmit}>
                  ÌöåÏõêÍ∞ÄÏûÖ
                </Button>
              </Form>
            </Formik>
            <Snackbar open={open} autoHideDuration={3000} onClose={handleClose}>
              <Alert
                onClose={handleClose}
                severity="success"
                sx={{ width: "100%" }}
              >
                ÌöåÏõêÍ∞ÄÏûÖ ÏôÑÎ£å!üòé
              </Alert>
            </Snackbar>
          </Stack>
        </S.InputContainer>
      </S.InputElementContainer>
    </S.FormInputContainer>
  );
});
