import axios from "axios";
import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "src/helper/AuthContext";
import { Button } from "src/components/common";
import * as S from "./styled";
import Snackbar from "@mui/material/Snackbar";
import IconButton from "@mui/material/IconButton";

export const FormInput = () => {
  const [open, setOpen] = React.useState(false);

  const handleClick = () => {
    setOpen(true);
  };

  const handleClose = (
    event: React.SyntheticEvent | Event,
    reason?: string
  ) => {
    if (reason === "clickaway") {
      return;
    }

    setOpen(false);
  };
  const [user_id, setUserid] = useState("");
  const [user_pw, setUserpw] = useState("");

  const navigate = useNavigate();

  const login = () => {
    const data = { user_id: user_id, user_pw: user_pw };
    axios.post("http://localhost:8080/auth/login", data).then((response) => {
      if (response.data.error) {
        alert(response.data.error);
      } else {
        navigate("/");
        localStorage.setItem("accessToken", response.data.token);
      }
    });
  };
  useEffect(() => {
    if (localStorage.getItem("accessToken")) {
      navigate("/");
      alert("로그인되었습니다.");
    }
  }, []);
  const enableButton = user_pw === "" ? true : false;
  return (
    <>
      <S.FormInputContainer>
        <S.InputElementContainer>
          <S.InputContainer>
            <S.InputElement
              type="text"
              onChange={(event) => {
                setUserid(event.target.value);
              }}
              placeholder="아이디"
            />
            <S.InputElement
              type="password"
              onChange={(event) => {
                setUserpw(event.target.value);
              }}
              placeholder="비밀번호"
              value={user_pw}
            />
            <Button
              disabled={enableButton}
              style={{
                backgroundColor: enableButton === true ? "#afafaf" : "black",
              }}
              onClick={login}
            >
              로그인
            </Button>
            <S.ChooseMenu>
              <S.Choose to="/auth/teacher">
                <S.goTeacher>선생님</S.goTeacher>로그인
              </S.Choose>
              <S.verticalChoose>|</S.verticalChoose>
              <S.Choose to="#">비밀번호 변경</S.Choose>
            </S.ChooseMenu>
          </S.InputContainer>
        </S.InputElementContainer>
      </S.FormInputContainer>
    </>
  );
};
