import React, { useEffect, useState, useContext } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

import * as S from "./styled";

export const ShowMyPlan = () => {
  let { id } = useParams(); // Ïû¨Ìï†Îãπ Í∞ÄÎä•
  let navigate = useNavigate();
  const [listOfPosts, setListOfPosts] = useState([]);
  const [listOfGoals, setListOfGoals] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:8080/todaytimes/byuserId/${id}`)
      .then((response) => {
        setListOfPosts(response.data);
      });
    axios
      .get(`http://localhost:8080/mygoal/byuserId/${id}`)
      .then((response) => {
        setListOfGoals(response.data);
      });

    if (!localStorage.getItem("accessToken")) {
      navigate("/auth/login");
      alert("Î°úÍ∑∏Ïù∏ÎêòÏñ¥ÏûàÎÇòÏöî?ü§î");
    }
  }, []);

  return (
    <>
      <S.Slider>
        <S.TotalContainer>
          <S.DayContainer>
            {listOfPosts.map((value, key) => {
              return (
                <div key={key}>
                  <S.DayTime
                    onClick={() => {
                      navigate(`/timedata/${value.id}`);
                    }}
                  >
                    {value.createdAt.slice(5, 10)}
                  </S.DayTime>
                  {value.kor + value.eng + value.kh}
                </div>
              );
            })}
          </S.DayContainer>
        </S.TotalContainer>
      </S.Slider>
    </>
  );
};
