import React, { useEffect, useState, useContext, useMemo } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { Bar } from "react-chartjs-2";

import { Horizontal, HorizontalContainer } from "../MyTime/styled";

import * as S from "./styled";

export const TimeData = () => {
  let { id } = useParams();
  const [timeData, setTimeData] = useState({});

  const subData = useMemo(
    () => [
      {
        id: 1,
        sub: "Íµ≠Ïñ¥",
        time: timeData.kor,
      },
      {
        id: 2,
        sub: "ÏòÅÏñ¥",
        time: timeData.eng,
      },
      {
        id: 3,
        sub: "ÏàòÌïô",
        time: timeData.math,
      },
      {
        id: 4,
        sub: "Í≥ºÌïô",
        time: timeData.sci,
      },
      {
        id: 5,
        sub: "ÏÇ¨Ìöå",
        time: timeData.com,
      },
      {
        id: 6,
        sub: "ÌïúÍµ≠ÏÇ¨",
        time: timeData.kh,
      },
      {
        id: 7,
        sub: "Í≥µÎ∂Ä",
        time: timeData.study,
      },
    ],
    [timeData]
  );

  const time = useMemo(
    () => ({
      labels: subData.map((val) => val.sub),
      datasets: [
        {
          label: "ÏãúÍ∞ÑÎç∞Ïù¥ÌÑ∞",
          data: subData.map((val) => val.time),
          backgroundColor: [
            "rgba(255, 99, 132, 0.2)",
            "rgba(255, 159, 64, 0.2)",
            "rgba(255, 205, 86, 0.2)",
            "rgba(75, 192, 192, 0.2)",
            "rgba(54, 162, 235, 0.2)",
            "rgba(153, 102, 255, 0.2)",
            "rgba(84, 221, 154, 0.2)",
          ],
          borderColor: [
            "rgb(255, 99, 132)",
            "rgb(255, 159, 64)",
            "rgb(255, 205, 86)",
            "rgb(75, 192, 192)",
            "rgb(54, 162, 235)",
            "rgb(153, 102, 255)",
            "rgb(84, 221, 154)",
          ],
          borderWidth: 1,
        },
      ],
    }),
    [subData]
  );

  useEffect(() => {
    axios
      .get(`http://52.79.235.48:8080/todaytimes/byId/${id}`)
      .then((res) => setTimeData(res.data));
  }, []);

  return (
    <>
      <S.TimeDataContainer>
        <S.TimeTextContainer>
          <S.TimeText>üóì {timeData.createdAt}</S.TimeText>
        </S.TimeTextContainer>
        <HorizontalContainer>
