        <S.TimeDataBoxContainer>
          <S.TimeDataBox>
            <S.TimeDataText>Íµ≠Ïñ¥: {timeData.kor}ÏãúÍ∞Ñ</S.TimeDataText>
            <S.TimeDataText>ÏòÅÏñ¥: {timeData.eng}ÏãúÍ∞Ñ</S.TimeDataText>
            <S.TimeDataText>ÏàòÌïô: {timeData.math}ÏãúÍ∞Ñ</S.TimeDataText>
            <S.TimeDataText>Í≥ºÌïô: {timeData.sci}ÏãúÍ∞Ñ</S.TimeDataText>
            <S.TimeDataText>ÏÇ¨Ìöå: {timeData.com}ÏãúÍ∞Ñ</S.TimeDataText>
            <S.TimeDataText>ÌïúÍµ≠ÏÇ¨: {timeData.kh}ÏãúÍ∞Ñ</S.TimeDataText>
            <S.TimeDataText>ÏùºÎ∞òÍ≥µÎ∂Ä: {timeData.study}ÏãúÍ∞Ñ</S.TimeDataText>
            <Bar data={time} />
          </S.TimeDataBox>
        </S.TimeDataBoxContainer>
