import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { AuthContext } from "src/helper/AuthContext";
import * as Yup from "yup";

import * as S from "./styled";

export interface FormInputProps
  extends React.InputHTMLAttributes<HTMLInputElement> {
  label: string;
}
export const FormInput = React.forwardRef<HTMLInputElement, FormInputProps>(
  ({ placeholder, type, name }, ref) => {
    const [user_id, setUser_id] = useState("");
    const [user_pw, setUser_pw] = useState("");
    const { setAuthState } = useContext(AuthContext);
    const login = () => {
      const data = { user_id: user_id, user_pw: user_pw };
      axios.post("http://localhost:8000/auth/login", data).then((response) => {
        if (response.data.error) {
          alert(response.data.error);
        } else {
          localStorage.setItem("accessToken", response.data);
          setAuthState(true);
          navigate("/");
        }
      });
    };
    const navigate = useNavigate();
    return (
      <S.FormInputContainer ref={ref}>
        <S.InputElementContainer>
          <S.InputContainer>
            <S.InputElement placeholder={placeholder} type={type} name={name} />
          </S.InputContainer>
        </S.InputElementContainer>
      </S.FormInputContainer>
    );
  }
);
