        ) : (
          <S.AuthContainer>
            <S.Flex>
              <S.auth>
                <S.AuthTitle style={{ marginTop: "0.2rem" }}>
                  {authState.user_name}
                </S.AuthTitle>
              </S.auth>
              <S.Logout onClick={FuncLogout}>
                <S.AuthTitle>Î°úÍ∑∏ÏïÑÏõÉ</S.AuthTitle>
              </S.Logout>
            </S.Flex>
          </S.AuthContainer>
        )}
        <S.SectionContainer>
          <S.SectionOne>
            <S.TitleContainer>
              <S.SectionTitle to="/time/today">
                Í≥µÎ∂ÄÏãúÍ∞Ñ Ï†ÅÏúºÎü¨ Í∞ÄÍ∏∞
