import React, { useState, useEffect } from "react";
import * as S from "./styled";
import axios from "axios";
import { useParams } from "react-router-dom";

export interface MyInfoTemplateProps {
  infoType: "myplan" | "profile";
  listToday: React.ReactNode;
  profile: React.ReactNode;
}

export const MyInfoTemplate: React.FC<MyInfoTemplateProps> = ({
  infoType,
  listToday,
  profile,
}) => {
  const [authState, setAuthState] = useState({ id: "" });
  useEffect(() => {
    axios
      .get("http://localhost:8080/auth/auth", {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        },
      })
      .then((response) => {
        if (response.data.error) {
          setAuthState({ ...authState, status: false });
          setTeaState({ ...teaState, status: false });
        } else {
          setAuthState({
            user_name: response.data.user_name,
            id: response.data.id,
            status: true,
          });
          setTeaState({
            t_job: response.data.t_job,
            status: true,
          });
        }
      });
  }, []);
  return (
    <>
      <S.ListOfMenu>
        <S.ListTypeContainer>
          <S.MyPlanContainer infoType="myplan">
            <S.TodayStudy to={`/myinfo/myplan/${authState.id}`}>
              마이플랜
            </S.TodayStudy>
          </S.MyPlanContainer>
          <S.ProfileContainer infoType="profile">
            <S.TodayStudy to="/myinfo/profile">프로필</S.TodayStudy>
          </S.ProfileContainer>
        </S.ListTypeContainer>
      </S.ListOfMenu>
      <S.FlexContainer>
        {infoType === "myplan" ? listToday : profile}
      </S.FlexContainer>
    </>
  );
};
