import React from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import * as Yup from "yup";

import * as S from "./styled";

export interface FormInputProps
  extends React.InputHTMLAttributes<HTMLInputElement> {
  label: string;
}
export const FormInput = React.forwardRef<HTMLInputElement, FormInputProps>(
  ({ placeholder, type, name }, ref) => {
    const navigate = useNavigate();
    const initialValues = {
      user_name: "",
      user_email: "",
      user_id: "",
      user_pw: "",
      user_ch_pw: "",
    };
    const validationSchema = Yup.object().shape({
      user_name: Yup.string().min(3).max(15).required(),
      user_email: Yup.string().min(3).max(50).required(),
      user_id: Yup.string().min(3).max(20).required(),
      user_pw: Yup.string().min(3).max(15).required(),
      user_ch_pw: Yup.string().min(3).max(15).required(),
    });
    const onSubmit = (data: any) => {
      axios.post("http://localhost:8000/auth", data).then(() => {
        console.log(data);
        navigate("/");
      });
    };
    return (
      <S.FormInputContainer ref={ref}>
        <S.InputElementContainer>
          <S.InputContainer>
            <Formik
              initialValues={initialValues}
              onSubmit={onSubmit}
              validationSchema={validationSchema}
            >
              <Field placeholder={placeholder} type={type} name={name} />
            </Formik>
          </S.InputContainer>
        </S.InputElementContainer>
      </S.FormInputContainer>
    );
  }
);
