import React, { useState } from "react";
import { useEffect } from "react";
import { host } from "src/LinkHost";

import * as S from "./styled";

import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
export const ProFile = () => {
  let { id } = useParams();
  const [authState, setAuthState] = useState({
    user_name: "",
    status: false,
  });
  const [timeData, setTimeData] = useState([
    {
      kor: 0,
      eng: 0,
      math: 0,
      sci: 0,
      com: 0,
      kh: 0,
      study: 0,
    },
  ]);
  const [listOfUsers, setListOfUsers] = useState([]);
  console.log(listOfUsers);
  const navigate = useNavigate();
  useEffect(() => {
    axios.get(`http://${host}:8080/auth`).then((res) => {
      setListOfUsers(res.data.id);
    });
    axios
      .get(`http://${host}:8080/todaytimes/api/data/${id}`)
      .then((response) => {
        setTimeData(response.data);
      });
    axios
      .get(`http://${host}:8080/auth/auth`, {
        headers: {
          accessToken: localStorage.getItem("accessToken") as any,
        },
      })
      .then((response) => {
        if (response.data.error) {
          setAuthState({ ...authState, status: false });
        } else {
          setAuthState({
            user_name: response.data.user_name,
            status: true,
          });
        }
      });
    if (!localStorage.getItem("accessToken")) {
      navigate("/auth/login");
      alert("Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.üòé");
    }
  }, []);
  let sum: number = 0;
  for (let i = 0; i < timeData.length; i++) {
    sum += timeData[i].kor;
    sum += timeData[i].eng;
    sum += timeData[i].math;
    sum += timeData[i].sci;
    sum += timeData[i].com;
    sum += timeData[i].kh;
    sum += timeData[i].study;
  }
  let avg: number = 0; //ÏÜåÏàòÏ†ê Î≤ÑÎ¶¨Í∏∞
  let t = "üèÜÌòÑÏû¨ Í≥µÎ∂ÄÏãúÍ∞Ñ 1Îì±Ïù¥ÏóêÏöî!";
  if (sum < 50) {
    t = "Ï¢Ä Îçî ÌôîÏù¥ÌåÖ!";
  }
  if (sum > 1) {
    avg = Math.floor(sum / timeData.length);
  }
  return (
    <>
      <S.Container>
        <S.ProfileName>{authState.user_name} ÌïôÏÉù</S.ProfileName>
        <S.HorizontalContainer>
          <S.Horizontal />
        </S.HorizontalContainer>
        <S.SumTime>ÌòÑÏû¨ÍπåÏßÄ ÎàÑÏ†Å Í≥µÎ∂ÄÏãúÍ∞Ñ: {sum}ÏãúÍ∞Ñ</S.SumTime>
        <S.SumTime>ÌïòÎ£®ÌèâÍ∑† Í≥µÎ∂ÄÏãúÍ∞Ñ: {avg}ÏãúÍ∞Ñ</S.SumTime>
        <S.SumTime>
          ÏàúÏúÑ: {listOfUsers}Î™ÖÏùò ÌïôÏÉùÏ§ë &nbsp;{t}
        </S.SumTime>
      </S.Container>
    </>
  );
};
