import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

import * as S from "./styled";

export const ShowMyPlan = () => {
  let { id } = useParams(); // Ïû¨Ìï†Îãπ Í∞ÄÎä•
  let navigate = useNavigate();
  const [listOfPosts, setListOfPosts] = useState([
    {
      id: 0,
      user_id: "",
      kor: 0,
      createdAt: "",
    },
  ]);

  //10Î≤à Ïú†Ï†ÄÏùò 3Î≤àÎç∞Ïù¥ÌÑ∞ Ï¶â :id/:idÎäêÎÇåÏúºÎ°ú Í∞ÄÏïºÌï†ÎìØ Ïù¥Í≤å ÎêòÎÇò?
  //ÏïÑÎãàÎ©¥ ÏûîÏ©å ÎπÑÌö®Ïú®Ï†ÅÏù¥Í≤å Ìïú ÌéòÏù¥ÏßÄÎ•º Îòê ÎßåÎì§Ïñ¥ÏÑú Ìï¥Î≥¥Ïûê ÎÇ¥Ïùº
  useEffect(() => {
    axios
      .get(`http://localhost:8080/todaytimes/byuserId/${id}`)
      .then((response) => {
        setListOfPosts(response.data);
      });

    if (!localStorage.getItem("accessToken")) {
      navigate("/auth/login");
      alert("Î°úÍ∑∏Ïù∏ÎêòÏñ¥ÏûàÎÇòÏöî?ü§î");
    }
  }, []);
  console.log(listOfPosts);
  return (
    <S.AllContainer>
      <S.Slider>
        <S.TotalContainer>
          <S.DayContainer>
            {listOfPosts.map((value: any, key: any) => {
              console.log(value.id);
              return (
                <div key={key}>
                  <S.DayTime
                    onClick={() => {
                      navigate(`/myinfo/myplan/${value.user_id}/${value.id}`); //Ïù¥ Í≤ΩÎ°úÏóêÎã§Í∞Ä Í∑∏ÎûòÌîÑ ÎùÑÏö∞ÎäîÍ±∞ ÌïòÎÇò ÎßåÎì§Ïñ¥ÎÜìÍ≥† Í∑∏ÎûòÌîÑÎ°ú Îì§Ïñ¥Í∞ÄÎäîÍ±¥ Í∑∏ÎÉ• Îî∞Î°úÌï¥ÏÑú ÎàåÎü¨ÏÑú Îì§Ïñ¥Í∞ÄÏïºÌï† Í≤É Í∞ôÎãπ.
                    }}
                  >
                    {value.createdAt.slice(5, 10)}
                  </S.DayTime>
                </div>
              );
            })}
          </S.DayContainer>
        </S.TotalContainer>
      </S.Slider>
    </S.AllContainer>
  );
};
