import React, { useEffect, useState, useContext, useMemo } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import { BarGraph } from "src/components";
import axios from "axios";

import * as S from "./styled";

export const ShowMyPlan = () => {
  let { id } = useParams(); // 재할당 가능
  let navigate = useNavigate();
  const [listOfPosts, setListOfPosts] = useState([
    {
      user_id: "",
      createdAt: "",
    },
  ]);
  const [Posts, setPosts] = useState({
    kor: 0,
    eng: 0,
    math: 0,
    sci: 0,
    com: 0,
    kh: 0,
    study: 0,
  });
  const [UserId, setUserId] = useState({});
  const [authState, setAuthState] = useState({
    id: 0,
    status: false,
  });
  const subData = useMemo(
    () => [
      {
        id: 1,
        sub: "국어",
        time: Posts.kor,
      },
      {
        id: 2,
        sub: "영어",
        time: Posts.eng,
      },
      {
        id: 3,
        sub: "수학",
        time: Posts.math,
      },
      {
        id: 4,
        sub: "과학",
        time: Posts.sci,
      },
      {
        id: 5,
        sub: "사회",
        time: Posts.com,
      },
      {
        id: 6,
        sub: "한국사",
        time: Posts.kh,
      },
      {
        id: 7,
        sub: "공부",
        time: Posts.study,
      },
    ],
    [Posts]
  );

  const time = useMemo(
    () => ({
      labels: subData.map((val) => val.sub),
      datasets: [
        {
          label: "시간데이터",
          data: subData.map((val) => val.time),
          backgroundColor: [
            "rgba(255, 99, 132, 0.2)",
            "rgba(255, 159, 64, 0.2)",
            "rgba(255, 205, 86, 0.2)",
            "rgba(75, 192, 192, 0.2)",
            "rgba(54, 162, 235, 0.2)",
            "rgba(153, 102, 255, 0.2)",
            "rgba(84, 221, 154, 0.2)",
          ],
          borderColor: [
            "rgb(255, 99, 132)",
            "rgb(255, 159, 64)",
            "rgb(255, 205, 86)",
            "rgb(75, 192, 192)",
            "rgb(54, 162, 235)",
            "rgb(153, 102, 255)",
            "rgb(84, 221, 154)",
          ],
          borderWidth: 1,
        },
      ],
    }),
    [subData]
  );

  //10번 유저의 3번데이터 즉 :id/:id느낌으로 가야할듯 이게 되나?
  //아니면 잔쩌 비효율적이게 한 페이지를 또 만들어서 해보자 내일
  useEffect(() => {
    axios
      .get(`http://localhost:8080/todaytimes/byuserId/${id}`)
      .then((response) => {
        setListOfPosts(response.data);
      });

    if (!localStorage.getItem("accessToken")) {
      navigate("/auth/login");
      alert("로그인되어있나요?🤔");
    }
    axios
      .get("http://localhost:8080/auth/auth", {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        },
      })
      .then((res) => {
        if (res.data.error) {
          setAuthState({ ...authState, status: false });
        } else {
          setAuthState({
            id: res.data.id,
            status: true,
          });
        }
      });
    axios.get(`http://localhost:8080/todaytimes/byId/${id}`).then((res) => {
      setUserId(res.data);
      setPosts(res.data);
    });
  }, []);

  let link = "0";
  return (
    <S.AllContainer>
      <S.Slider>
        <S.TotalContainer>
          <S.DayContainer>
            {listOfPosts.map((value: any, key: any) => {
              return (
                <div key={key}>
                  <S.DayTime
                    onClick={() => {
                      navigate(`/myinfo/myplan/${value.user_id}/${value.id}`);
                    }}
                  >
                    {value.createdAt.slice(5, 10)}
                  </S.DayTime>
                </div>
              );
            })}
          </S.DayContainer>
        </S.TotalContainer>
      </S.Slider>
      {link === "0" ? <BarGraph chartData={time} /> : <></>}
    </S.AllContainer>
  );
  /**
   * 에러가 나는 이유는 byId에서 불러올 id가 경로에 필요한데 그 경로를 jung으로 받아오니까 데이터를 못불러옴
   * 그래서 파일 경로 하나를 더 만들어서 ㅇㄴ 근데 애초에 이러면 둘다 동시에 못불러오는디..
   * 왜냐면 하나는 jung으로 받아오고 하나는 id로 받아와서 그래프가 있는 파일에서는
   * 날짜 리스트를 못받아오잖아
   */
};
