import React, { useState, useEffect } from "react";
import { Route, Routes } from "react-router-dom";
import axios from "axios";

import { MyTime, TimeData, TodayTime, HabitTime } from "src/components";
import { StList } from "src/components";
import { MainPage, TimesPage } from "./pages";
import { InfoPage } from "./pages/info";
import { AuthPage } from "./pages/auth";
import { TeacherForm } from "src/components";

export const App: React.FC = () => {
  const [authState, setAuthState] = useState({
    user_name: "",
    id: "",
    status: false,
  });

  const [teaState, setTeaState] = useState({
    t_job: "",
    status: false,
  });

  useEffect(() => {
    axios
      .get("http://localhost:8080/auth/auth", {
        headers: {
          accessToken: localStorage.getItem("accessToken") as any,
        },
      })
      .then((response) => {
        if (response.data.error) {
          setAuthState({ ...authState, status: false });
          setTeaState({ ...teaState, status: false });
        } else {
          setAuthState({
            user_name: response.data.user_name,
            id: response.data.id,
            status: true,
          });
          setTeaState({
            t_job: response.data.t_job,
            status: true,
          });
        }
      });
  }, []);

  const logout = () => {
    localStorage.removeItem("accessToken");
    setAuthState({ user_name: "", id: "", status: false });
    setTeaState({ t_job: "", status: false });
  };
  return (
    <>
      <Routes>
        <Route index element={<MainPage />} />
        <Route path="time">
          <Route path="today" element={<TimesPage />} />
          <Route path="habit" element={<TimesPage />} />
          <Route path="goal" element={<TimesPage />} />
        </Route>
        <Route path="timedata/:id" element={<TimeData />} />
        <Route path="auth">
          <Route path="login" element={<AuthPage />} />
          <Route path="register" element={<AuthPage />} />
          <Route path="teacher" element={<TeacherForm />} />
        </Route>
        <Route path="myinfo">
          <Route path="myplan/:id" element={<InfoPage />} />
          <Route path="profile/:id" element={<InfoPage />} />
        </Route>
        <Route path="st">
          <Route path="list" element={<StList />} />
        </Route>
      </Routes>
    </>
  );
};
