import React, { useState } from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { useLocation } from "react-router-dom";
import * as Yup from "yup";
import axios from "axios";
import { useNavigate } from "react-router-dom";

import { FormInput, AuthFormTemplate, RegisterForm } from "src/components";

import * as S from "./styled";

export const AuthPage: React.FC = () => {
  const location = useLocation();
  const formType = location.pathname.split("/")[2];
  const navigate = useNavigate();
  const initialValues = {
    user_name: "",
    user_email: "",
    user_id: "",
    user_pw: "",
    user_ch_pw: "",
  };
  const validationSchema = Yup.object().shape({
    user_name: Yup.string().min(3).max(15).required(),
    user_email: Yup.string().min(3).max(50).required(),
    user_id: Yup.string().min(3).max(20).required(),
    user_pw: Yup.string().min(3).max(15).required(),
    user_ch_pw: Yup.string().min(3).max(15).required(),
  });

  const onSubmit = (data: any) => {
    axios.post("http://localhost:8000/auth", data).then(() => {
      console.log(data);
      navigate("/");
    });
  };

  return (
    <S.AuthPageFormContainer>
      <AuthFormTemplate
        formType={formType as any}
        loginForm={
          <>
            <Formik
              initialValues={initialValues}
              onSubmit={onSubmit}
              validationSchema={validationSchema}
            >
              <ErrorMessage name="user_id" component="span" />
              <Field name="user_id" placeholder="아이디" type="text" />
              <ErrorMessage name="user_pw" component="span" />
              <Field name="user_pw" placeholder="비밀번호" type="password" />
            </Formik>
          </>
        }
        registerForm={
          <>
            <RegisterForm
              label="이름"
              placeholder="이름을 입력해주세요."
              type="text"
              name="user_name"
            />
            <RegisterForm
              label="이메일"
              placeholder="이메일을 입력해주세요. (선택, 광고수신)"
              type="email"
              name="user_email"
            />
            <RegisterForm
              label="아이디"
              placeholder="아이디 입력해주세요."
              type="text"
              name="user_id"
            />
            <RegisterForm
              label="비밀번호"
              placeholder="비밀번호 입력해주세요."
              type="password"
              name="user_pw"
            />
            <RegisterForm
              label="비밀번호 확인"
              placeholder="비밀번호 입력해주세요."
              type="password"
              name="user_ch_pw"
            />
          </>
        }
        onSubmit={onSubmit}
      />
      /&gt;
    </S.AuthPageFormContainer>
  );
};
