import React, { useEffect } from "react";
import { Formik, Form } from "formik";
import * as Yup from "yup";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Button } from "src/components";

import * as S from "./styled";

export const GoalTime = () => {
  const navigate = useNavigate();
  const initialValues = {
    GoalMorning: "",
    GoalNight: "",
  };
  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/");
      alert("Î°úÍ∑∏Ïù∏Ìï¥Ïïº Ïù¥Ïö© Í∞ÄÎä•Ìïú ÏÑúÎπÑÏä§ÏûÖÎãàÎã§.");
    }
  }, []);
  const validationSchema = Yup.object().shape({
    GoalMorning: Yup.string().required("Î™©ÌëúÍ∏∞ÏÉÅÏãúÍ∞Ñ"),
    GoalNight: Yup.string().required("Î™©ÌëúÏ∑®Ïπ®ÏãúÍ∞Ñ"),
  });

  const onSubmit = (data) => {
    axios
      .post("http://localhost:8080/mygoal", data, {
        headers: { accessToken: localStorage.getItem("accessToken") },
      })
      .then((response) => {
        alert("Ïò§ÎäòÎèÑ ÏàòÍ≥†ÌñàÏñ¥Ïöî!üòÄ");
        navigate("/");
      });
  };
  return (
    <>
      <S.InputTimeContainer>
        <S.InputContainer>
          <Formik
            initialValues={initialValues}
            onSubmit={onSubmit}
            validationSchema={validationSchema}
          >
            <Form style={{ display: "flex", flexDirection: "column" }}>
              <label>Î™©ÌëúÍ∏∞ÏÉÅÏãúÍ∞ÑH</label>
              <S.InputTime autoComplete="off" name="GoalMorning" />
              <label>Î™©ÌëúÏ∑®Ïπ®ÏãúÍ∞ÑH</label>
              <S.InputTime autoComplete="off" name="GoalNight" />
              <Button style={{ width: "13.5rem" }} type="submit">
                Ï†úÏ∂ú
              </Button>
            </Form>
          </Formik>
        </S.InputContainer>
      </S.InputTimeContainer>
    </>
  );
};
