import React, { useState, useEffect } from "react";
import Stack from "@mui/material/Stack";
import MuiAlert, { AlertProps } from "@mui/material/Alert";
import Snackbar from "@mui/material/Snackbar";
import axios from "axios";
import { useNetwork } from "src/hooks/useNetwork";
import { host } from "src/LinkHost";

const Alert = React.forwardRef<HTMLDivElement, AlertProps>(function Alert(
  props,
  ref
) {
  return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});
export const SnackBar = () => {
  const [open, setOpen] = React.useState(true);
  const [authState, setAuthState] = useState({
    status: false,
  });
  const handleClose = (
    event?: React.SyntheticEvent | Event,
    reason?: string
  ) => {
    if (reason === "clickaway") {
      return;
    }

    setOpen(false);
  };
  useEffect(() => {
    axios
      .get(`http://${host}:8080/auth/auth`, {
        headers: {
          accessToken: localStorage.getItem("accessToken") as any,
        },
      })
      .then((res) => {
        if (res.data.error) {
          setAuthState({ ...authState, status: false });
        } else {
          setAuthState({
            status: true,
          });
        }
      });
  }, []);
  const handleNetworkChange = (online: any) => {
    console.log(online ? "online" : "offline");
  };
  const onLine = useNetwork(handleNetworkChange);
  return (
    <Stack spacing={2} sx={{ width: "100%" }}>
      <Snackbar
        open={open}
        anchorOrigin={{ vertical: "bottom", horizontal: "right" }}
        transitionDuration={{ exit: 800, enter: 300 }}
      >
        {!authState.status ? (
          <Alert
            onClose={handleClose}
            severity="info"
            sx={{ width: "24rem", fontSize: "1.3rem", fontWeight: "bold" }}
          >
            Î°úÍ∑∏Ïù∏ÎêòÏñ¥ÏûàÏßÄ ÏïäÏùÄ ÏÉÅÌÉúü§î
          </Alert>
        ) : (
          <Alert
            onClose={handleClose}
            severity="success"
            sx={{ width: "22rem", fontSize: "1.3rem", fontWeight: "bold" }}
          >
            Î°úÍ∑∏Ïù∏ ÎêòÏñ¥ÏûàÏùåüòé
          </Alert>
        )}
      </Snackbar>
    </Stack>
  );
};
