import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Formik, Form } from "formik";
import { host } from "src/LinkHost";
import { Button } from "src/components/common";
import * as Yup from "yup";
import axios from "axios";
import * as S from "./styled";

export interface RegisterFormInputProps
  extends React.InputHTMLAttributes<HTMLInputElement> {}
export const RegisterForm = React.forwardRef<
  HTMLInputElement,
  RegisterFormInputProps
>(({ autoComplete, placeholder, type, name }, ref) => {
  const initialValues = {
    user_name: "",
    user_id: "",
    user_pw: "",
    user_agree: "",
  };

  const navigate = useNavigate();

  const validationSchema = Yup.object().shape({
    user_name: Yup.string().required(),
    user_id: Yup.string().required(),
    user_pw: Yup.string().required(),
    user_agree: Yup.string().required(),
  });

  const onSubmit = (data: any) => {
    axios.post(`http://${host}:8080/auth`, data).then(() => {
      console.log(data);
      navigate("/auth/login");
    });
  };

  useEffect(() => {
    if (localStorage.getItem("accessToken")) {
      navigate("/");
      alert("로그인되어 있습니다.");
    }
  }, []);

  return (
    <S.FormInputContainer ref={ref}>
      <S.InputElementContainer>
        <S.InputContainer>
          <Formik
            initialValues={initialValues}
            onSubmit={onSubmit}
            validationSchema={validationSchema}
          >
            <Form>
              <S.Label>이름</S.Label>
              <S.InputElement
                autoComplete="off"
                name="user_name"
                placeholder="이름"
              />
              <S.Label>아이디</S.Label>
              <S.InputElement
                type="text"
                autoComplete="off"
                name="user_id"
                placeholder="id"
              />
              <S.Label>비밀번호</S.Label>
              <S.InputElement
                autoComplete="off"
                name="user_pw"
                placeholder="pw"
                type="password"
              />
              <S.AgreeCheck type="checkbox" name="user_agree" value="checked" />{" "}
              개인정보 수집 및 이용 동의
              <Button type="submit">회원가입</Button>
            </Form>
          </Formik>
        </S.InputContainer>
      </S.InputElementContainer>
    </S.FormInputContainer>
  );
});
