import React, { useEffect, useState, useMemo } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

import { Horizontal, HorizontalContainer } from "../MyTime/styled";

import * as S from "./styled";
import { BarGraph, CircleGraph, LineGraph } from "../GraphList";
import { host } from "src/LinkHost";

export const TimeData = () => {
  let { id } = useParams();
  const [timeData, setTimeData] = useState({});
  const [habitData, setHabitData] = useState({});
  const [goalData, setGoalData] = useState({});
  const [authState, setAuthState] = useState({
    id: 0,
    user_id: "",
    status: false,
  });
  const [teaState, setTeaState] = useState({
    status: false,
  });
  const [listOfPosts, setListOfPosts] = useState({
    createdAt: "",
  });
  const listStyleAm = useMemo(() => [
    {
      id: 1,
      habitAm: "Í∏∞ÏÉÅÏãúÍ∞ÑAM",
      habitTimeAm: habitData.morningAm,
    },
    {
      id: 2,
      habitAm: "Ï∑®Ïπ®ÏãúÍ∞ÑAM",
      habitTimeAm: habitData.nightAm,
    },
  ]);
  const lifeStylePm = useMemo(
    () => [
      {
        id: 1,
        habit: "Í∏∞ÏÉÅÏãúÍ∞ÑPM",
        habitTime: habitData.morning,
      },
      {
        id: 2,
        habit: "Ï∑®Ïπ®ÏãúÍ∞ÑPM",
        habitTime: habitData.night,
      },
      {
        id: 3,
        habit: "ÎÇÆÏû†ÏãúÍ∞ÑH",
        habitTime: habitData.sleep,
      },
      {
        id: 4,
        habit: "Ìú¥ÏãùÏãúÍ∞ÑH",
        habitTime: habitData.break,
      },
    ],
    [habitData]
  );
  const goalInfo = useMemo(
    () => [
      {
        id: 1,
        goal: "Î™©ÌëúÍ∏∞ÏÉÅÏãúÍ∞ÑAM",
        goalTime: goalData.GoalMorning,
      },
      {
        id: 2,
        goal: "Î™©ÌëúÏ∑®Ïπ®ÏãúÍ∞ÑPM",
        goalTime: goalData.GoalNight,
      },
    ],
    [goalData]
  );
  const subData = useMemo(
    () => [
      {
        id: 1,
        sub: "Íµ≠Ïñ¥",
        time: timeData.kor,
      },
      {
        id: 2,
        sub: "ÏòÅÏñ¥",
        time: timeData.eng,
      },
      {
        id: 3,
        sub: "ÏàòÌïô",
        time: timeData.math,
      },
      {
        id: 4,
        sub: "Í≥ºÌïô",
        time: timeData.sci,
      },
      {
        id: 5,
        sub: "ÏÇ¨Ìöå",
        time: timeData.com,
      },
      {
        id: 6,
        sub: "ÌïúÍµ≠ÏÇ¨",
        time: timeData.kh,
      },
      {
        id: 7,
        sub: "Í≥µÎ∂Ä",
        time: timeData.study,
      },
    ],
    [timeData]
  );
  const goalGraphData = useMemo(
    () => ({
      labels: goalInfo.map((val) => val.goal),
      datasets: [
        {
          label: "ÏãúÍ∞ÑÎç∞Ïù¥ÌÑ∞",
          data: goalInfo.map((val) => val.goalTime),
          backgroundColor: [
            "rgba(255, 99, 132, 0.2)",
            "rgba(255, 159, 64, 0.2)",
            "rgba(255, 205, 86, 0.2)",
            "rgba(75, 192, 192, 0.2)",
            "rgba(54, 162, 235, 0.2)",
            "rgba(153, 102, 255, 0.2)",
            "rgba(84, 221, 154, 0.2)",
          ],
          borderColor: [
            "rgb(255, 99, 132)",
            "rgb(255, 159, 64)",
            "rgb(255, 205, 86)",
            "rgb(75, 192, 192)",
            "rgb(54, 162, 235)",
            "rgb(153, 102, 255)",
            "rgb(84, 221, 154)",
          ],
          borderWidth: 1,
        },
      ],
    }),
    [goalInfo]
  );
  const lifeStyleData = useMemo(
    () => ({
      labels: lifeStylePm.map((val) => val.habit),

      datasets: [
        {
          label: "Ïò§Ï†Ñ ÏãúÍ∞ÑÎç∞Ïù¥ÌÑ∞",
          data: listStyleAm.map((val) => val.habitTimeAm),
          backgroundColor: ["rgba(255, 159, 64, 0.2)"],
          borderColor: ["rgb(255, 159, 64)"],
        },
        {
          label: "Ïò§ÌõÑ ÏãúÍ∞ÑÎç∞Ïù¥ÌÑ∞",
          data: lifeStylePm.map((val) => val.habitTime),
          backgroundColor: ["rgba(255, 99, 132, 0.2)"],
          borderColor: ["rgb(255, 99, 132)"],
        },
      ],
    }),
    [lifeStylePm, listStyleAm]
  );
  const time = useMemo(
    () => ({
      labels: subData.map((val) => val.sub),
      datasets: [
        {
          label: "ÏãúÍ∞Ñ Îç∞Ïù¥ÌÑ∞",
          borderRadius: 10,
          data: subData.map((val) => val.time),
          backgroundColor: [
            "rgba(255, 99, 132, 0.2)",
            "rgba(255, 159, 64, 0.2)",
            "rgba(255, 205, 86, 0.2)",
            "rgba(75, 192, 192, 0.2)",
            "rgba(54, 162, 235, 0.2)",
            "rgba(153, 102, 255, 0.2)",
            "rgba(84, 221, 154, 0.2)",
          ],
          borderColor: [
            "rgb(255, 99, 132)",
            "rgb(255, 159, 64)",
            "rgb(255, 205, 86)",
            "rgb(75, 192, 192)",
            "rgb(54, 162, 235)",
            "rgb(153, 102, 255)",
            "rgb(84, 221, 154)",
          ],
          borderWidth: 1,
        },
      ],
    }),
    [subData]
  );

  useEffect(() => {
    axios
      .get(`http://${host}:8080/auth/auth`, {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        },
      })
      .then((res) => {
        if (res.data.error) {
          setAuthState({ ...authState, status: false });
          setTeaState({ ...teaState, status: false });
        } else {
          setAuthState({
            id: res.data.id,
            user_id: res.data.user_id,
            status: true,
          });
          setTeaState({
            status: true,
          });
        }
      });
    axios.get(`http://${host}:8080/myhabit/byId/${id}`).then((res) => {
      setHabitData(res.data);
    });
    axios.get(`http://${host}:8080/mygoal/byId/${id}`).then((res) => {
      setGoalData(res.data);
    });
    axios.get(`http://${host}:8080/todaytimes/byId/${id}`).then((res) => {
      setListOfPosts(res.data);
      setTimeData(res.data);
    });
  }, []);
  console.log(authState.user_id, "Îã§Î•¥ÏûêÎÇò");
  console.log(listOfPosts.user_id, "Í∞úÏÑ∏Ïïº");
  return (
    <>
      <S.TimeDataContainer>
        <S.BackSpaceContainer>
          <S.BackSpace to={`/myinfo/myplan/${authState.user_id}`}>
            &lt;-
          </S.BackSpace>
        </S.BackSpaceContainer>
        <S.TimeTextContainer>
          <S.TimeText>
            {listOfPosts.user_id}
            {listOfPosts.createdAt.slice(5, 7)}Ïõî{" "}
            {listOfPosts.createdAt.slice(8, 10)}Ïùº Îç∞Ïù¥ÌÑ∞
          </S.TimeText>
        </S.TimeTextContainer>
        <S.TitleContainer style={{ marginTop: "8rem" }}>
          <S.TitleText>Í≥µÎ∂ÄÏãúÍ∞Ñ</S.TitleText>
        </S.TitleContainer>
        <HorizontalContainer>
          <Horizontal />
        </HorizontalContainer>
        {authState.user_id === listOfPosts.user_id || teaState === true ? (
          <S.TimeDataBoxContainer>
            <S.GraphSize>
              <BarGraph chartData={time} />
            </S.GraphSize>
            <S.TitleContainer style={{ marginTop: "8rem" }}>
              <S.TitleText>ÏÉùÌôúÏäµÍ¥Ä</S.TitleText>
            </S.TitleContainer>
            <HorizontalContainer>
              <Horizontal />
            </HorizontalContainer>
            <S.GraphSize>
              <LineGraph chartData={lifeStyleData} />
            </S.GraphSize>
            <S.TitleContainer style={{ marginTop: "8rem" }}>
              <S.TitleText>ÏäµÍ¥ÄÎ™©Ìëú</S.TitleText>
            </S.TitleContainer>
            <HorizontalContainer>
              <Horizontal />
            </HorizontalContainer>
            <S.GraphSize>
              <CircleGraph
                style={{ height: "60%" }}
                chartData={goalGraphData}
              />
            </S.GraphSize>
          </S.TimeDataBoxContainer>
        ) : (
          <div
            style={{
              textAlign: "center",
              marginTop: "3rem",
              fontSize: "2rem",
            }}
          >
            Îã§Î•∏ ÌïôÏÉùÏùò Îç∞Ïù¥ÌÑ∞Ïóê Ï†ëÍ∑ºÌñàÏñ¥Ïöîü§î
          </div>
        )}
      </S.TimeDataContainer>
    </>
  );
};
