import React, { useState, useMemo } from "react";
import { useEffect } from "react";

import * as S from "./styled";

import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
export const ProFile = () => {
  let { id } = useParams();
  const [authState, setAuthState] = useState({
    user_name: "",
    status: false,
  });
  const [timeData, setTimeData] = useState([
    {
      kor: 0,
      eng: 0,
    },
  ]);

  const navigate = useNavigate();
  useEffect(() => {
    axios
      .get(`http://localhost:8080/todaytimes/api/data/${id}`)
      .then((response) => {
        setTimeData(response.data);
      });
    axios
      .get("http://localhost:8080/auth/auth", {
        headers: {
          accessToken: localStorage.getItem("accessToken") as any,
        },
      })
      .then((response) => {
        if (response.data.error) {
          setAuthState({ ...authState, status: false });
        } else {
          setAuthState({
            user_name: response.data.user_name,
            status: true,
          });
        }
      });
    if (!localStorage.getItem("accessToken")) {
      navigate("/auth/login");
      alert("Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.üòé");
    }
  }, []);
  let a = 0;
  let b = 0;
  for (let i = 0; i < timeData.length; i++) {
    console.log(timeData[i].kor);
    a += timeData[i].kor;
    b += timeData[i].eng;
  }
  console.log("Í≤∞Í≥ºÎ¨º: ", a);
  console.log("ÏòÅÏñ¥: ", b);
  console.log(timeData[0], "here");
  return (
    <>
      <S.Container>
        <S.ProfileName>{authState.user_name}</S.ProfileName>
        <S.SumTime>Ï†ÑÏ≤¥ ÎàÑÏ†Å Í≥µÎ∂ÄÏãúÍ∞Ñ:{a}</S.SumTime>
      </S.Container>
    </>
  );
};
