import React, { useState, useMemo } from "react";
import { useEffect } from "react";

import * as S from "./styled";

import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
export const ProFile: React.FC = () => {
  let id = useParams();
  const [authState, setAuthState] = useState({
    user_name: "",
    status: false,
  });
  const [timeData, setTimeData] = useState({
    kor: "",
    eng: "",
    math: "",
    sci: "",
    com: "",
    kh: "",
    study: "",
  });

  const navigate = useNavigate();
  useEffect(() => {
    axios
      .get(`http://localhost:8080/todaytimes/byId/${id}`)
      .then((res) => setTimeData(res.data));
    axios
      .get("http://localhost:8080/auth/auth", {
        headers: {
          accessToken: localStorage.getItem("accessToken") as any,
        },
      })
      .then((response) => {
        if (response.data.error) {
          setAuthState({ ...authState, status: false });
        } else {
          setAuthState({
            user_name: response.data.user_name,
            status: true,
          });
        }
      });
    if (!localStorage.getItem("accessToken")) {
      navigate("/auth/login");
      alert("Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.üòé");
    }
  }, []);
  const subData = useMemo(
    () => [
      {
        id: 1,
        time: timeData.kor,
      },
      {
        id: 2,
        time: timeData.eng,
      },
      {
        id: 3,
        time: timeData.math,
      },
      {
        id: 4,
        time: timeData.sci,
      },
      {
        id: 5,
        time: timeData.com,
      },
      {
        id: 6,
        time: timeData.kh,
      },
      {
        id: 7,
        time: timeData.study,
      },
    ],
    [timeData]
  );
  const Sum = { subData };
  console.log(Sum);
  return (
    <>
      <S.Container>
        <S.ProfileName>{authState.user_name}</S.ProfileName>
        <S.SumTime>Ï¥ù Í≥µÎ∂ÄÏãúÍ∞Ñ: </S.SumTime>
      </S.Container>
    </>
  );
};
