import React, { useEffect, useState, useContext, useMemo } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { BarGraph } from "src/components";
import axios from "axios";

import * as S from "./styled";

export const ShowMyPlan = () => {
  let { id } = useParams(); // Ïû¨Ìï†Îãπ Í∞ÄÎä•
  let navigate = useNavigate();
  const [listOfPosts, setListOfPosts] = useState([
    {
      createdAt: "",
    },
  ]);

  const [timeData, setTimeData] = useState({
    kor: 0,
    eng: 0,
    math: 0,
    sci: 0,
    com: 0,
    kh: 0,
    study: 0,
  });
  const subData = useMemo(
    () => [
      {
        id: 1,
        sub: "Íµ≠Ïñ¥",
        time: timeData.kor,
      },
      {
        id: 2,
        sub: "ÏòÅÏñ¥",
        time: timeData.eng,
      },
      {
        id: 3,
        sub: "ÏàòÌïô",
        time: timeData.math,
      },
      {
        id: 4,
        sub: "Í≥ºÌïô",
        time: timeData.sci,
      },
      {
        id: 5,
        sub: "ÏÇ¨Ìöå",
        time: timeData.com,
      },
      {
        id: 6,
        sub: "ÌïúÍµ≠ÏÇ¨",
        time: timeData.kh,
      },
      {
        id: 7,
        sub: "Í≥µÎ∂Ä",
        time: timeData.study,
      },
    ],
    [timeData]
  );

  const time = useMemo(
    () => ({
      labels: subData.map((val) => val.sub),
      datasets: [
        {
          label: "ÏãúÍ∞ÑÎç∞Ïù¥ÌÑ∞",
          data: subData.map((val) => val.time),
          backgroundColor: [
            "rgba(255, 99, 132, 0.2)",
            "rgba(255, 159, 64, 0.2)",
            "rgba(255, 205, 86, 0.2)",
            "rgba(75, 192, 192, 0.2)",
            "rgba(54, 162, 235, 0.2)",
            "rgba(153, 102, 255, 0.2)",
            "rgba(84, 221, 154, 0.2)",
          ],
          borderColor: [
            "rgb(255, 99, 132)",
            "rgb(255, 159, 64)",
            "rgb(255, 205, 86)",
            "rgb(75, 192, 192)",
            "rgb(54, 162, 235)",
            "rgb(153, 102, 255)",
            "rgb(84, 221, 154)",
          ],
          borderWidth: 1,
        },
      ],
    }),
    [subData]
  );

  useEffect(() => {
    axios
      .get(`http://localhost:8080/todaytimes/byId/${id}`) //10Î≤à Ïú†Ï†ÄÏùò 3Î≤àÎç∞Ïù¥ÌÑ∞
      .then((res) => setTimeData(res.data));
    axios
      .get(`http://localhost:8080/todaytimes/byuserId/${id}`)
      .then((response) => {
        setListOfPosts(response.data);
      });

    if (!localStorage.getItem("accessToken")) {
      navigate("/auth/login");
      alert("Î°úÍ∑∏Ïù∏ÎêòÏñ¥ÏûàÎÇòÏöî?ü§î");
    }
  }, []);

  return (
    <>
      <S.Slider>
        <S.TotalContainer>
          <S.DayContainer>
            {listOfPosts.map((value: any, key: any) => {
              return (
                <div key={key}>
                  <S.DayTime
                    onClick={() => {
                      navigate(`/myinfo/myplan/${value.id}`);
                    }}
                  >
                    {value.createdAt.slice(5, 10)}
                  </S.DayTime>
                </div>
              );
            })}
          </S.DayContainer>
        </S.TotalContainer>
      </S.Slider>
    </>
  );
};
